"use client";

import "popser/styles";

import { useTheme } from "next-themes";
import { Toaster as PopserToaster } from "popser";
import type { ToasterProps } from "popser";

const Toaster = ({
  richColors = true,
  style,
  ...props
}: Partial<ToasterProps>) => {
  const { theme = "system" } = useTheme();

  return (
    <PopserToaster
      theme={theme as ToasterProps["theme"]}
      richColors={richColors}
      style={
        {
          "--popser-bg": "var(--popover)",
          "--popser-fg": "var(--popover-foreground)",
          "--popser-border": "var(--border)",
          "--popser-radius": "var(--radius)",
          "--popser-shadow":
            "0 4px 12px color-mix(in oklch, var(--foreground) 10%, transparent)",
          ...style,
        } as React.CSSProperties
      }
      {...props}
    />
  );
};

export { Toaster };
